import { Platform, ThemeTypography, Variant } from '@mtnkente/design-tokens';
export interface ThemePlatform {
    [Platform.CORE]: ThemeVariant;
    [Platform.PLAY]: ThemeVariant;
    [Platform.MOMO]: ThemeVariant;
}
export interface ThemeVariant {
    [Variant.LIGHT]: Theme;
    [Variant.DARK]: Theme;
}
export interface Theme {
    typography: ThemeTypography;
    colors: {
        [key: string]: string;
    };
    floats: {
        [key: string]: string;
    };
}
